# # # Definition of the Ckrige-methods# # # Christoph Hofer, 30-03-2010if (!isGeneric("CKrige"))	setGeneric( name = "CKrige", function(formula, data, locations, object, ...)		standardGeneric("CKrige")	    )"CKrige.polygons" <- function(formula, data, locations, object,  method = 2, ex.out = F){    f.polygons.CKrige(formula = formula, data = data, locations = locations,	object = object, method = method, ex.out = ex.out)}#setMethod("CKrige", c(	"formula", "data.frame", "formula", "preCKrigePolygons"),		    CKrige.polygons		)#"CKrige.points" <- function(formula, data, locations, object, method = 2, ex.out = F){    f.points.CKrige(formula = formula, data = data, locations = locations,      		object = object, method = method, ex.out = ex.out)}#setMethod("CKrige", c(	"formula", "data.frame", "formula", "preCKrigePoints"),		    CKrige.points		)#