# # # Definition of the preCkrige-methods # # # Christoph Hofer, 30-03-2010if (!isGeneric("preCKrige"))	setGeneric(name = "preCKrige", def = function(newdata, neighbours , model, ...)		standardGeneric("preCKrige"))# # # # # # 	    # # # 	    "preCKrige.polygonsDF" <- function( newdata, neighbours, model, pwidth = 0, pheight = 0, napp = 1){#    f.preCKrige.check.and.test( newdata = newdata, neighbours = neighbours, model = model, pwidth = pwidth, pheight = pheight, napp = napp )        f.polygons.preCKrige(newdata = newdata, neighbours = neighbours, model = model,	pwidth = pwidth, pheight = pheight, napp =napp)}#setMethod("preCKrige",	c("SpatialPolygonsDataFrame", "ANY", "covmodel"), preCKrige.polygonsDF)# # # # # # # # # "preCKrige.pointsDF"  <- function( newdata, neighbours , model) {#       f.preCKrige.check.and.test( newdata, neighbours, model, pwidth = 0, pheight =0, napp = 1)     f.points.preCKrige( newdata = newdata, neighbours = neighbours, model = model)}#setMethod("preCKrige",	c("SpatialPointsDataFrame", "ANY", "covmodel"), preCKrige.pointsDF)# # # # # # # # # "preCKrige.polygons" <- function( newdata, neighbours,     					 model, pwidth = 0, pheight = 0, napp = 1){#    f.preCKrige.check.and.test( newdata, neighbours, model, pwidth, pheight, napp )        f.polygons.preCKrige( newdata = newdata, neighbours = neighbours, model = model,	pwidth = pwidth, pheight = pheight, napp = napp )}#setMethod( "preCKrige", c( "SpatialPolygons", "ANY", "covmodel"), preCKrige.polygons)# # # # # # # # # "preCKrige.points"  <- function( newdata, neighbours, model ){      f.preCKrige.check.and.test( newdata, neighbours, model, pwidth = 0, pheight =0, napp = 1)     f.points.preCKrige( newdata = newdata, neighbours = neighbours, model = model )}setMethod("preCKrige",	c("SpatialPoints", "ANY", "covmodel"), preCKrige.points)